import { useKeenSlider } from 'keen-slider/react'
import 'keen-slider/keen-slider.min.css'
import { useState } from 'react'
import { Box, Typography } from '@mui/material'

export default function TestimonialCard() {
  const [currentSlide, setCurrentSlide] = useState(0)
  const [loaded, setLoaded] = useState(false)
  const [sliderRef, instanceRef] = useKeenSlider({
    initial: 0,
    slideChanged(slider) {
      setCurrentSlide(slider.track.details.rel)
    },
    created() {
      setLoaded(true)
    }
  })
  return (
    <>
      <Box sx={{ display: 'flex', alignItems: 'center', gap: '1em', marginTop: { xs: '30px', md: '0' } }}>
        {/* ===================================== Previous Arrow ==================== */}
        {loaded && instanceRef.current && (
          <>
            <Arrow
              left
              onClick={e => e.stopPropagation() || instanceRef.current?.prev()}
              disabled={currentSlide === 0}
            />
          </>
        )}

        {/* =========================== SLider ====================== */}
        <div ref={sliderRef} className='keen-slider'>
          <Box
            className='keen-slider__slide'
            sx={{ position: 'relative', padding: { xs: '1em', md: '7em 2em' }, width: '90%', margin: '0px auto' }}
          >
            <Typography
              component={'span'}
              sx={{ position: 'absolute', top: 0, left: 0, display: { xs: 'none', md: 'block' } }}
            >
              <svg
                width={60}
                height={146}
                viewBox='0 0 146 146'
                fill='none'
                xmlns='http://www.w3.org/2000/svg'
                xmlnsXlink='http://www.w3.org/1999/xlink'
              >
                <mask
                  id='mask0_100_8046'
                  style={{ maskType: 'alpha' }}
                  maskUnits='userSpaceOnUse'
                  x={0}
                  y={0}
                  width={146}
                  height={146}
                >
                  <rect
                    x={146}
                    y={146}
                    width={146}
                    height={146}
                    transform='rotate(180 146 146)'
                    fill='url(#pattern0_100_8046)'
                  />
                </mask>
                <g mask='url(#mask0_100_8046)'>
                  <rect
                    x={146}
                    y={146}
                    width={146}
                    height={146}
                    transform='rotate(180 146 146)'
                    fill='#E8FD1C'
                    fillOpacity='0.56'
                  />
                </g>
                <defs>
                  <pattern id='pattern0_100_8046' patternContentUnits='objectBoundingBox' width={1} height={1}>
                    <use xlinkHref='#image0_100_8046' transform='scale(0.00195312)' />
                  </pattern>
                  <image
                    id='image0_100_8046'
                    width={512}
                    height={512}
                    xlinkHref='data:image/png;base64,'
                  />
                </defs>
              </svg>
            </Typography>
            <Typography
              sx={{
                color: '#0A5247',
                fontWeight: 'DM Sans',
                fontSize: '20px',
                lineHeight: '35px',
                textAlign: 'justify'
              }}
            >
              I first tried Grub It's Chicken Tikka Masala from the nearest tube station to my work, and I was blown
              away! The sauce was rich and flavorful, with just the right amount of spice. The convenience and taste
              have made me a repeat customer. Highly recommend!
            </Typography>
            <Box sx={{ display: 'flex', flexDirection: { xs: 'column' }, gap: '1em', marginTop: '2em' }}>
              <Typography sx={{ fontFamily: 'DM Sans', fontWeight: '700', fontSize: '24px', color: '#0A5247' }}>
                Sharlene Rose
              </Typography>

              <Typography sx={{ fontFamily: 'DM Sans', fontWeight: '400', fontSize: '24px', color: '#0A5247' }}>
                Loyal Customer
              </Typography>
            </Box>
          </Box>
          <Box
            className='keen-slider__slide'
            sx={{ position: 'relative', padding: { xs: '1em', md: '7em 2em' }, width: '90%', margin: '0px auto' }}
          >
            <Typography
              component={'span'}
              sx={{ position: 'absolute', top: 0, left: 0, display: { xs: 'none', md: 'block' } }}
            >
              <svg
                width={60}
                height={146}
                viewBox='0 0 146 146'
                fill='none'
                xmlns='http://www.w3.org/2000/svg'
                xmlnsXlink='http://www.w3.org/1999/xlink'
              >
                <mask
                  id='mask0_100_8046'
                  style={{ maskType: 'alpha' }}
                  maskUnits='userSpaceOnUse'
                  x={0}
                  y={0}
                  width={146}
                  height={146}
                >
                  <rect
                    x={146}
                    y={146}
                    width={146}
                    height={146}
                    transform='rotate(180 146 146)'
                    fill='url(#pattern0_100_8046)'
                  />
                </mask>
                <g mask='url(#mask0_100_8046)'>
                  <rect
                    x={146}
                    y={146}
                    width={146}
                    height={146}
                    transform='rotate(180 146 146)'
                    fill='#E8FD1C'
                    fillOpacity='0.56'
                  />
                </g>
                <defs>
                  <pattern id='pattern0_100_8046' patternContentUnits='objectBoundingBox' width={1} height={1}>
                    <use xlinkHref='#image0_100_8046' transform='scale(0.00195312)' />
                  </pattern>
                  <image
                    id='image0_100_8046'
                    width={512}
                    height={512}
                    xlinkHref='data:image/png;base64,'
                  />
                </defs>
              </svg>
            </Typography>
            <Typography
              sx={{
                color: '#0A5247',
                fontWeight: 'DM Sans',
                fontSize: '20px',
                lineHeight: '35px',
                textAlign: 'justify'
              }}
            >
              I first tried Grub It's Chicken Tikka Masala from the nearest tube station to my work, and I was blown
              away! The sauce was rich and flavorful, with just the right amount of spice. The convenience and taste
              have made me a repeat customer. Highly recommend!
            </Typography>
            <Box sx={{ display: 'flex', gap: '1em', marginTop: '2em', flexDirection: { xs: 'column' } }}>
              <Typography sx={{ fontFamily: 'DM Sans', fontWeight: '700', fontSize: '24px', color: '#0A5247' }}>
                Sharlene Rose
              </Typography>

              <Typography sx={{ fontFamily: 'DM Sans', fontWeight: '400', fontSize: '24px', color: '#0A5247' }}>
                Loyal Customer
              </Typography>
            </Box>
          </Box>
        </div>

        {/* ============================= Next Arrow =============== */}
        {loaded && instanceRef.current && (
          <>
            <Arrow
              onClick={e => e.stopPropagation() || instanceRef.current?.next()}
              disabled={currentSlide === instanceRef.current.track.details.slides.length - 1}
            />
          </>
        )}
      </Box>
    </>
  )
}

function Arrow(props) {
  const disabled = props.disabled ? ' arrow--disabled' : ''
  return (
    <>
      {props.left && (
        <svg
          width='50'
          onClick={props.onClick}
          height='50'
          viewBox='0 0 50 50'
          fill='none'
          xmlns='http://www.w3.org/2000/svg'
        >
          <rect y='50' width='50' height='50' rx='25' transform='rotate(-90 0 50)' fill='#F56700' fill-opacity='0.3' />
          <path
            d='M29 33L21 24.5L29 16'
            stroke='#C8F8F1'
            stroke-width='4'
            stroke-linecap='round'
            stroke-linejoin='round'
          />
        </svg>
      )}
      {!props.left && (
        <svg
          width='50'
          onClick={props.onClick}
          height='50'
          viewBox='0 0 50 50'
          fill='none'
          xmlns='http://www.w3.org/2000/svg'
        >
          <rect width='50' height='50' rx='25' transform='matrix(0 -1 -1 0 50 50)' fill='#F56700' />
          <path
            d='M21 33L29 24.5L21 16'
            stroke='#C8F8F1'
            stroke-width='4'
            stroke-linecap='round'
            stroke-linejoin='round'
          />
        </svg>
      )}
    </>
  )

  // <svg
  //   onClick={props.onClick}
  //   width={50}
  //   className={`arrow ${props.left ? 'arrow--left' : 'arrow--right'} ${disabled}`}
  //   xmlns='http://www.w3.org/2000/svg'
  //   viewBox='0 0 24 24'
  // >
  //   {props.left && <path d='M16.67 0l2.83 2.829-9.339 9.175 9.339 9.167-2.83 2.829-12.17-11.996z' />}
  //   {!props.left && <path d='M5 3l3.057-3 11.943 12-11.943 12-3.057-3 9-9z' />}
  // </svg>
}
